export function assembleFetchRequestKey(url, lazy, options) {
  const operation = lazy ? "lazy-fetch" : "fetch";
  const parts = [
    "sanctum",
    operation,
    url,
    options?.method ?? "get",
    JSON.stringify({
      query: options?.query ?? {},
      body: options?.body ?? {}
    })
  ];
  return parts.join(":");
}
