import { assembleFetchRequestKey } from "../utils/fetch.js";
import { useLazyAsyncData, useSanctumClient } from "#imports";
export function useLazySanctumFetch(url, options, asyncDataOptions, key) {
  const client = useSanctumClient();
  const fetchKey = key ?? assembleFetchRequestKey(url, true, options);
  return useLazyAsyncData(
    fetchKey,
    () => client(url, options),
    asyncDataOptions
  );
}
