import { assembleFetchRequestKey } from "../utils/fetch.js";
import { useAsyncData, useSanctumClient } from "#imports";
export function useSanctumFetch(url, options, asyncDataOptions, key) {
  const client = useSanctumClient();
  const fetchKey = key ?? assembleFetchRequestKey(url, false, options);
  return useAsyncData(
    fetchKey,
    () => client(url, options),
    asyncDataOptions
  );
}
