export const isFile = (value) => typeof File !== "undefined" && value instanceof File || value instanceof Blob || typeof FileList !== "undefined" && value instanceof FileList && value.length > 0;
export const hasFiles = (data) => isFile(data) || typeof data === "object" && data !== null && Object.values(data).some((value) => hasFiles(value));
export const clearFiles = (data) => {
  let newData = { ...data };
  Object.keys(newData).forEach((name) => {
    const value = newData[name];
    if (value === null) {
      return;
    }
    if (isFile(value)) {
      const { [name]: _, ...fields } = newData;
      newData = fields;
      return;
    }
    if (Array.isArray(value)) {
      newData[name] = Object.values(clearFiles({ ...value }));
      return;
    }
    if (typeof value === "object") {
      newData[name] = clearFiles(newData[name]);
      return;
    }
  });
  return newData;
};
